mapper.insert:
INSERT INTO
    exp
        (exp_seq, user_seq, exp_title, exp_content
        , exp_start, exp_end
        , exp_duration
        , exp_get_start, exp_get_end
        , exp_min, exp_max
        , exp_addr, exp_addr_detail
        , exp_img, exp_price
        , exp_request_datetime, exp_status)
    VALUES
        (0, #{userSeq}, #{expTitle}, #{expContent}
        , #{expStart}, #{expEnd}
        , CONCAT(
            IF(TIMESTAMPDIFF(minute, #{expStart}, #{expEnd}) >= 60, CONCAT(TRUNCATE(TIMESTAMPDIFF(minute, #{expStart}, #{expEnd}) / 60, 0), '시간'), ''),
            ' ',
            IF(TIMESTAMPDIFF(minute, #{expStart}, #{expEnd}) % 60 > 0, CONCAT(TIMESTAMPDIFF(minute, #{expStart}, #{expEnd}) % 60, '분'), '')
        )
        , #{expGetStart}, #{expGetEnd}
        , #{expMin}, #{expMax}
        , #{expAddr}, #{expAddrDetail}
        , #{expImg}, #{expPrice}
        , now(), #{expStatus})
